plt.rcParams['font.sans-serif'] = ['SimHei']  #解决中文显示乱码问题
plt.rcParams['font.family'] = 'sans-serif' #设置字体格式
plt.rcParams['axes.unicode_minus'] = False

data = pd.read_csv('ADULT_IID_LF_ga.csv')

plt.axis([0, 100, 0, 100])
y1data = data.loc[:, 'fdfl']
y2data = data.loc[:, 'fedavg']
y3data = data.loc[:, 'median']
y4data = data.loc[:, 'tmean']
y5data = data.loc[:, 'mkrum']
y6data = data.loc[:, 'foolsgold']
y7data = data.loc[:, 'ffl']

plt.plot(y1data, color='#FA8072', label=u'fdfl')
plt.plot(y2data, color='#FFA500', label=u'fedavg')
plt.plot(y3data, color='#40E0D0', label=u'median')
plt.plot(y4data, color='#87CEFA', label=u'tmean')
plt.plot(y5data, color='#FFFFE0', label=u'mkrum')
plt.plot(y6data, color='#00FFFF', label=u'foolsgold')
plt.plot(y7data, color='#98FB98', label=u'ffl')
plt.xlabel(xlabel='epochs', fontsize=15)
plt.ylabel(ylabel='global accuracy', fontsize=15)
plt.savefig(r'ADULT_IID_LF_ga')
plt.show()

